    private void btnCalcularActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnCalcularActionPerformed
        // TODO add your handling code here:
        double salMen = Double.parseDouble(txtSalMen.getText());
        int anio = Integer.parseInt(txtanio.getText());
        int mes = Integer.parseInt(txtmes.getText());
        int dia = Integer.parseInt(txtdia.getText());
        
        double salPromMen = (salMen*14)/12;
        double salPromDia = salPromMen/30;
        
        double pagAnio = salPromMen * anio;
        
        int cantDia = (mes*30)+dia;
        int diasProm = (cantDia*30)/360;
        double pagDia = salPromDia * diasProm;
        
        double totalPago = pagAnio+pagDia;
        
        lblCesantia.setText(String.valueOf(Math.round(totalPago)));
    }//GEN-LAST:event_btnCalcularActionPerformed

